sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    var groupName = g_form.getValue('assignment_group');

    var membersGA = new GlideAjax('NeedItUtils');
    membersGA.addParam('sysparm_name', 'countGroupMembers');
    membersGA.addParam('sysparm_group_id', groupName);
    membersGA.getXMLAnswer(membersParse);

    function membersParse(response) {
		g_form.setValue('u_group_member_count', response);
    }

}
