sys_ui_action.script:
(function(current, previous, gs, action) {

	var user = current.caller_id;
    var gdt = new GlideDateTime();
	gdt.addDaysLocalTime(1);

    var needIt = new GlideRecord('x_58872_needit_needit');
    needIt.setValue("u_requested_for", user);
    needIt.setValue("u_requested_for_email", current.caller_id.email);
    needIt.setValue("u_request_type", "platform");
    needIt.setValue("u_what_needed", "sn_config");
    needIt.setValue("u_when_needed", gdt);
    needIt.setValue("short_description", current.short_description);
    needIt.setValue("description", current.description);
    needIt.setValue("priority", current.priority);
    //needIt.setValue("cmdb_ci", current.cmdb_ci);
    //needIt.setValue("sys_domain", current.sys_domain);
    //needIt.setValue("company", current.company);
    needIt.setValue('state', 13);
    var sysId = needIt.insert();
    current.update();

    var incUrl = "<a href='" + current.getLink(true) + "'>" + current.getDisplayValue() + "</a>";
    gs.addInfoMessage(gs.getMessage("NeedIt request {0} created from {1}", [needIt.getValue("number"), incUrl]));

    action.setReturnURL(current);
    action.setRedirectURL(needIt.getGlideRecord());

})(current, previous, gs, action);
